import React from 'react';
import { Link } from 'react-router-dom';
import { CardView } from 'react-card-with-image';
import { v4 as uuidv4 } from 'uuid';
import './Companies.css';

    const initialList = [
        {
            id: 'a',
            name: 'Lorem ipsum',
            description:
                'dolor sit amet, consectetur adipiscing elit. Sed tempus nunc et tincidunt lobortis. Aliquam placerat',

        },
        {
            id: 'b',
            name: 'Dennis',
        },
        {
            id: 1,
            header: '',
            description:
                'dolor sit amet, consectetur adipiscing elit. Sed tempus nunc et tincidunt lobortis. Aliquam placerat',
        },
        {
            id: 2,
            header: 'Sed cursus',
            description:
                'in metus quis tempor. Donec at venenatis magna, vel fringilla dui. Curabitur id gravida ipsum.',
        },
        {
            id: 3,
            header: 'Sed fermentum',
            description:
                'condimentum purus, non sagittis massa faucibus id. Sed finibus convallis lectus eu fringilla.',
        },
        {
            id: 4,
            header: 'Proin et urna',
            description:
                'vitae neque fermentum fringilla. Proin bibendum sollicitudin aliquet. Fusce id magna aliquam.',
        },
        {
            id: 5,
            header: 'Cras leo velit',
            description:
                'finibus id eros eu, commodo sollicitudin lacus. Nunc semper enim nec est viverra.',
        }
    ]
    const Companies = () => {
        const [list, setList] = React.useState(initialList);
        const [header, setHeader] = React.useState('');

        function handleChange(event) {
            setHeader(event.target.value);
        }

        function handleAdd() {
            const newList = list.concat({ header, id: uuidv4() });
            // const newList = list.concat({ header });

            setList(newList);
            setList('');
        }

        return (
            <>
                {/*<CardView*/}
                {/*    initialList={initialList}*/}
                {/*    activeColor='#000'*/}
                {/*    imageHeight='650px'*/}
                {/*    imageWidth='800px'*/}
                {/*/>*/}
                <div>
                    <AddItem
                        name={name}
                        onChange={handleChange}
                        onAdd={handleAdd}
                    />

                    <List list={list} />
                    <div>
                        <input type="text" value={header} onChange={handleChange} />
                        <button type="button" onClick={handleAdd}>
                            Add
                        </button>
                    </div>
                    <ul>
                        {list.map((item) => (
                            <li key={item.id}>{item.name}</li>
                        ))}
                    </ul>
                </div>
                    <li><Link to="/detail">more info about companies</Link></li>


            </>
        )
    };
const AddItem = ({ name, onChange, onAdd }) => (
    <div>
        <input type="text" value={name} onChange={onChange} />
        <button type="button" onClick={onAdd}>
            Add
        </button>
    </div>
);
const List = ({ list }) => (
    <ul>
        {list.map((item) => (
            <li key={item.id}>{item.name}</li>
        ))}
    </ul>
);
export default Companies;
