import React from 'react'
import { CardView } from 'react-card-with-image'
import 'react-card-with-image/dist/index.css'

import './Companies.css';
const Companies = () => {
    const items = [
        {
            id: 1,
            header: 'Lorem ipsum',
            description: 'dolor sit amet, consectetur adipiscing elit. Sed tempus nunc et tincidunt lobortis. Aliquam placerat, justo sit amet mattis molestie, ipsum nisi congue turpis, in imperdiet nisi nisl sit amet arcu.',
            image: 'data:image/jpeg;base64,'
        },
        {
            id: 2,
            header: 'Sed cursus',
            description:
                'in metus quis tempor. Donec at venenatis magna, vel fringilla dui.',
            image: 'image-src'
        },
        {
            id: 3,
            header: 'Sed fermentum',
            description:
                'condimentum purus, non sagittis massa faucibus id. Sed finibus convallis lectus eu fringilla.',
            image: 'image-src'
        },
        {
            id: 4,
            header: 'Proin et urna',
            description:
                'vitae neque fermentum fringilla. Proin bibendum sollicitudin aliquet.',
            image: 'image-src'
        },
        {
            id: 5,
            header: 'Cras leo velit',
            description:
                'finibus id eros eu, commodo sollicitudin lacus. Nunc semper enim nec est viverra.',
            image: 'image-src'
        }
    ]
    return (
        <>
        <CardView
            items={items}
            activeColor='#000'
            imageHeight='650px'
            imageWidth='800px'
        />
    <li><Link to="/detail">more info about companies</Link></li>
            </>
    )
};
export default Companies;
