import React from 'react';
import { Link } from 'react-router-dom';
import { CardView } from 'react-card-with-image';
import './Companies.css';

function Companies(props) {
    const items = [
        {
            id: 1,
            header: 'Lorem ipsum',
            description:
                'dolor sit amet, consectetur adipiscing elit. Sed tempus nunc et tincidunt lobortis. Aliquam placerat',
        },
        {
            id: 2,
            header: 'Sed cursus',
            description:
                'in metus quis tempor. Donec at venenatis magna, vel fringilla dui. Curabitur id gravida ipsum.',
        },
        {
            id: 3,
            header: 'Sed fermentum',
            description:
                'condimentum purus, non sagittis massa faucibus id. Sed finibus convallis lectus eu fringilla.',
        },
        {
            id: 4,
            header: 'Proin et urna',
            description:
                'vitae neque fermentum fringilla. Proin bibendum sollicitudin aliquet. Fusce id magna aliquam.',
        },
        {
            id: 5,
            header: 'Cras leo velit',
            description:
                'finibus id eros eu, commodo sollicitudin lacus. Nunc semper enim nec est viverra, at pharetra orci lobortis. Nulla facilisi. Sed non lectus nunc.',
        }
    ]

        return (
            <>
                <CardView
                    items={items}
                    activeColor='#fff'
                    imageHeight='650px'
                    imageWidth='800px'
                />
                <ul className='itemsList'>
                    {items.map((item) => (
                        <li key={item.id}>{item.name}</li>
                    ))}
                    <li><Link to="/detail">more info about companies</Link></li>
                </ul>

            </>
        )
    };
export default Companies;
