            const value = e.target.value.replace( /^(\+)?\d+(\.[05])?$/);
            setAge(value);
            const obj = e.target.value.replace( /^(\+)?\d+(\.[05])?$/);
            setNumber(value);
        };

        return (
            <>
                <div className="App">
                    <input value={age} onChange={handleChange} />
                </div>
                <br/>
                <div className="App">
                    <input value={number} onChange={handleChange} />
                </div>
            </>
        );
    }
